CREATE TABLE #temp
(
	Rn INT IDENTITY,
	Code NVARCHAR(200),
	DrugName NVARCHAR(4000),
	DosageForm NVARCHAR(4000)
)



select * ,
(select t2.code from #temp t2 where t2.rn = 
                    (select max(t3.rn) from #temp t3 where t3.rn <=t1.rn and t3.code !='') )
from #temp t1

SELECT * FROM #temp t1
	INNER JOIN (SELECT * FROM #temp t2 where t2.Rn < t1.Rn) t3 ON t3.Rn < t1.Rn --AND t2.Code = t1.Code


